export default {
    formatPrice(value) {
      return `$${Number(value).toFixed(2)}`;
    },
    capitalizeFirst(str) {
      return str.charAt(0).toUpperCase() + str.slice(1);
    },
    slugify(text) {
      return text.toLowerCase().replace(/\s+/g, '-');
    }
  }
